    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        /*Para usar a classe de funções é necessário adicionar uma Exceção, para que se der alguma erro
         * o mesmo possa ser tratado.
         * Como não é possível alterar a linha que declara este método foi necessário inserir o função Try Catch
         */
        try {
            clsFerramentaTurno fTurno = new clsFerramentaTurno();
            fTurno.HabilitaNovo(txtCodTurno, txtHoraIni, txtObs, optSeis, optOito, btnSalvar, btnAlterar);

            clsFunctions function = new clsFunctions();
            clsCadTurnoDAO CadTurnoDao = new clsCadTurnoDAO();

            txtCodTurno.setText(Integer.toString(function.ProxRegistro("CC_CAD_TURNO", "ID_TURNO")));

            function.carregaTabela(CadTurnoDao.ListaTurno(), tblTurno); //carrega a tabela de turno ao abrir tela

        } catch (Exception err) {
            JOptionPane.showMessageDialog(this,
                    "Ocorreram problemas ao abrir tela.\n(" + err.getMessage() + ")", "Atenção!", JOptionPane.ERROR_MESSAGE);
        }

    }//GEN-LAST:event_formWindowOpened

    private void btnSalvarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnSalvarMouseClicked

        // TODO add your handling code here:
        
        if (btnSalvar.isEnabled()!=false){

        try {
            clsFerramentaTurno fTurno = new clsFerramentaTurno();
            
            //Verifica validação dos campos
            if (txtHoraIni.getText().equals("  :  :00")) {
                JOptionPane.showMessageDialog(this, "Preencha o Campo Hora Inicial!",
                        "Atenção", JOptionPane.INFORMATION_MESSAGE);
            } else if ((fTurno.verificaData(txtHoraIni.getText())) == false) {
                JOptionPane.showMessageDialog(this, "Hora Inválida", "Atenção", JOptionPane.INFORMATION_MESSAGE);
                txtHoraIni.setText("");

            } else if (txtObs.getText().isEmpty()) {
                JOptionPane.showMessageDialog(this, "Preencha o Campo Observações!!",
                        "Atenção", JOptionPane.INFORMATION_MESSAGE);
            } else {


                //verifica qual option esta selecionado e transforma de boolean para inteiro
                boolean bStatus = optSeis.isSelected();

                int iStatus, valorHora;

                if (bStatus == true) {
                    iStatus = 1;
                    valorHora = 6;
                } else {
                    iStatus = 0;
                    valorHora = 8;
                }

                //valida data final
                String sdataFim;
                sdataFim = fTurno.alteraData(txtHoraIni.getText(), valorHora);

                clsCadTurno CadTurno = new clsCadTurno(Integer.parseInt(txtCodTurno.getText()),
                        txtHoraIni.getText(),
                        sdataFim,
                        txtObs.getText(),
                        iStatus);

                clsCadTurnoDAO CadTurnoDao = new clsCadTurnoDAO();


                CadTurnoDao.InserirTurno(CadTurno);

                fTurno.HabilitaNovo(txtCodTurno, txtHoraIni, txtObs, optSeis, optOito, btnSalvar, btnAlterar);

                clsFunctions function = new clsFunctions();
                function.carregaTabela(CadTurnoDao.ListaTurno(), tblTurno); //atualiza a tabela de turno ao salvar registro

            }//finaliza validação dos campos

        } catch (Exception err) {
            JOptionPane.showMessageDialog(this,
                    "Não foi possível salvar/alterar este registro.\n(" + err.getMessage() + ")", "Atenção!", JOptionPane.ERROR_MESSAGE);
        }
        }
    }//GEN-LAST:event_btnSalvarMouseClicked

    private void btnExcluirMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnExcluirMouseClicked
        // TODO add your handling code here:
        try {
            clsFunctions function = new clsFunctions();

            int codigo = function.getRowTable(tblTurno);

            clsCadTurnoDAO CadTurnoDao = new clsCadTurnoDAO();

            CadTurnoDao.ExcluiTurno(codigo);

            txtCodTurno.setText(Integer.toString(function.ProxRegistro("CC_CAD_TURNO", "ID_TURNO")));
            txtHoraIni.setText("");
            txtObs.setText("");

            function.carregaTabela(CadTurnoDao.ListaTurno(), tblTurno); //atualiza a tabela de turno ao excluir registro

        } catch (Exception err) {
            JOptionPane.showMessageDialog(this,
                    "Não foi possível realizar Exclusão!", "Atenção!", JOptionPane.INFORMATION_MESSAGE);


        }
    }//GEN-LAST:event_btnExcluirMouseClicked

