    private void txtCodTurnoKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtCodTurnoKeyPressed
        // TODO add your handling code here:
        try {

            clsCadTurnoDAO CadTurnoDAO = new clsCadTurnoDAO();
            if (evt.getKeyCode() == KeyEvent.VK_ENTER) {

                ResultSet rst = CadTurnoDAO.ConsultarTurno(Integer.parseInt(txtCodTurno.getText()));
                txtHoraIni.setText(rst.getString("HORA_INI"));
                txtObs.setText(rst.getString("OBS_TURNO"));


                if (rst.getBoolean("TEMPO") == true) {
                    optSeis.setSelected(true);

                } else {
                    optOito.setSelected(true);
                }
                btnAlterar.setEnabled(true);
            }

        } catch (Exception ex) {
            JOptionPane.showMessageDialog(this,
                    "O turno com o Código: " + txtCodTurno.getText()
                    + ", não foi encontrado!", "Atenção", JOptionPane.INFORMATION_MESSAGE);




        }
    }//GEN-LAST:event_txtCodTurnoKeyPressed

